import React, { useState } from 'react';


const Form = () =>
{
    const initialState = {
        noun: '',
        noun2: "",
        adj: "",
        adj2: ''
    };



    // const INITIAL_STATE = 
    const [ formData, setFormData ] = useState( initialState );

    const history = [ [],
    [ `There was a  ${ formData.adj2 } ${ formData.noun2 } who loved a ${ formData.adj } ${ formData.noun } ` ],

    [ `Once upon a time , a ${ formData.adj }  ${ formData.noun } was born , some say this creature will defeat the ${ formData.adj2 }  ${ formData.noun2 } ` ]
    ];
    const idx = Math.floor( Math.random() * history.length );
    const choice = history[ idx ];



    const handleChange = evt =>
    {
        const { name, value } = evt.target;
        setFormData( fData => ( {
            ...fData,
            [ name ]: value
        } ) );
    };
    const handleSubmit = evt =>
    {
        evt.preventDefault();
        console.log( "Check out state ->", formData );

        // do something with the data submitted
        // const { noun, noun2, adj, adj2 } = formData;
        alert( choice );
        setFormData( initialState );
    };



    return ( <>
        <form onSubmit={ handleSubmit } >
            <label htmlFor="Word">First:</label>
            <input
                id="noun"
                name="noun"
                value={ formData.noun }
                onChange={ handleChange }
            />
            <label htmlFor="Word">Second:</label>
            <input
                id="noun2"
                name="noun2"
                value={ formData.noun2 }
                onChange={ handleChange }
            />

            <label htmlFor="Word">First:</label>
            <input
                id="firstAdj"
                name="adj"
                value={ formData.adj }
                onChange={ handleChange }
            /><label htmlFor="Word">First Adjective:</label>
            <input
                id="secondAdj"
                name="adj2"
                value={ formData.adj2 }
                onChange={ handleChange }
            />


            <button>Add a new person!</button>
            {/* <p> There was a { formData.adj2 } { formData.noun2 } who loved a { formData.adj } { formData.noun } </p> */ }
        </form>

    </>
    );
};

export default Form;
