import React, { useState, useEffect } from 'react';
import History from './History';


const Form = () =>
{
    const initialState = {
        noun: "",
        noun2: "",
        adj: "",
        adj2: ""
    };



    // const INITIAL_STATE = 
    const [ formData, setFormData ] = useState( initialState );
    const [ toggle, setToggle ] = Toggle( false );


    // const history = [ [ `In a far away country the ${ formData.adj } ${ formData.noun } was getting ready to figth the ${ formData.adj2 } ${ formData.noun2 }` ],
    // [ `There was a  ${ formData.adj2 } ${ formData.noun2 } who loved a ${ formData.adj } ${ formData.noun } ` ],

    // [ `Once upon a time , a ${ formData.adj }  ${ formData.noun } was born , some say this creature will defeat the ${ formData.adj2 }  ${ formData.noun2 } ` ]
    // ];
    // const idx = Math.floor( Math.random() * history.length );
    // const choice = history[ idx ];



    const handleChange = evt =>
    {
        const { name, value } = evt.target;
        setFormData( fData => ( {
            ...fData,
            [ name ]: value
        } ) );

    };
    const handleSubmit = evt =>
    {
        evt.preventDefault();
        console.log( "Check out state ->", formData );

        // do something with the data submitted
        // const { noun, noun2, adj, adj2 } = formData;
        // alert( choice );




        setFormData( initialState );
    };



    return ( <>
        <form onSubmit={ handleSubmit } >

            <input
                id="noun"
                name="noun"
                value={ formData.noun }
                onChange={ handleChange }
                placeholder='First Noun'
            />

            <input
                id="noun2"
                name="noun2"
                value={ formData.noun2 }
                onChange={ handleChange }
                placeholder='Second Noun'
            />


            <input
                id="firstAdj"
                name="adj"
                value={ formData.adj }
                onChange={ handleChange }
                placeholder='First Adjective'
            />
            <input
                id="secondAdj"
                name="adj2"
                value={ formData.adj2 }
                onChange={ handleChange }
                placeholder='Second Adjective'
            />


            <button>Create History</button>
            {/* <p> There was a { formData.adj2 } { formData.noun2 } who loved a { formData.adj } { formData.noun } </p> */ }
        </form>

        < History formData={ formData } toggle={ toggle } />
    </>
    );
};

export default Form;
