import React, { useState } from 'react';

const Form = () =>
{
    // const handleSubmit = evt =>
    // {
    //     evt.preventDefault;
    // };
    const [ formData, setFormData ] = useState( {
        noun: '',

        noun2: ""


    } );


    const handleChange = evt =>
    {
        const { name, value } = evt.target;
        setFormData( fData => ( {
            ...fData,
            [ name ]: value
        } ) );
    };

    return (
        <form >
            <label htmlFor="Word">First:</label>
            <input
                id="noun"
                name="noun"
                value={ formData.noun }
                onChange={ handleChange }
            />
            <label htmlFor="Word">Second:</label>
            <input
                id="noun2"
                name="noun2"
                value={ formData.noun2 }
                onChange={ handleChange }
            />

            <label htmlFor="Word">First:</label>
            <input
                id="firstAdj"
                name="noun"
                value={ formData.adj }
                onChange={ handleChange }
            /><label htmlFor="Word">First Adjective:</label>
            <input
                id="secondAdj"
                name="adj2"
                value={ formData.adj2 }
                onChange={ handleChange }
            />


            <button>Add a new person!</button>
        </form>
    );
};

export default Form;
