import React, { useState } from 'react';

const Form = () =>
{
    const handleSubmit = evt =>
    {
        evt.preventDefault();
        console.log( "Check out state ->", formData );
        const history = `There was a ${ formData.adj2 } ${ formData.noun2 } who loved a ${ formData.adj } ${ formData.noun } `;
        // do something with the data submitted
        return history;
    };

    const [ formData, setFormData ] = useState( {
        noun: '',

        noun2: "",
        adj: "",
        adj2: ''


    } );


    const handleChange = evt =>
    {
        const { name, value } = evt.target;
        setFormData( fData => ( {
            ...fData,
            [ name ]: value
        } ) );
    };

    return ( <>
        <form onSubmit={ handleSubmit } >
            <label htmlFor="Word">First:</label>
            <input
                id="noun"
                name="noun"
                value={ formData.noun }
                onChange={ handleChange }
            />
            <label htmlFor="Word">Second:</label>
            <input
                id="noun2"
                name="noun2"
                value={ formData.noun2 }
                onChange={ handleChange }
            />

            <label htmlFor="Word">First:</label>
            <input
                id="firstAdj"
                name="adj"
                value={ formData.adj }
                onChange={ handleChange }
            /><label htmlFor="Word">First Adjective:</label>
            <input
                id="secondAdj"
                name="adj2"
                value={ formData.adj2 }
                onChange={ handleChange }
            />


            <button>Add a new person!</button>
        </form>
        <h1>history</h1>
    </>
    );
};

export default Form;
