import React, { useState } from 'react';
// import History from './History';

const Form = () =>
{
    const handleSubmit = evt =>
    {
        evt.preventDefault();
        console.log( "Check out state ->", formData );

        // do something with the data submitted
        // const { noun, noun2, adj, adj2 } = formData;
        return ( <h1>
            There was a { adj2 } { noun2 } who loved a { adj } { noun };</h1> );
    };




    const INITIAL_STATE = {
        noun: '',
        noun2: "",
        adj: "",
        adj2: ''
    };
    const [ formData, setFormData ] = useState( INITIAL_STATE );



    const handleChange = evt =>
    {
        const { name, value } = evt.target;
        setFormData( fData => ( {
            ...fData,
            [ name ]: value
        } ) );
    };

    return ( <>
        <form onSubmit={ handleSubmit } >
            <label htmlFor="Word">First:</label>
            <input
                id="noun"
                name="noun"
                value={ formData.noun }
                onChange={ handleChange }
            />
            <label htmlFor="Word">Second:</label>
            <input
                id="noun2"
                name="noun2"
                value={ formData.noun2 }
                onChange={ handleChange }
            />

            <label htmlFor="Word">First:</label>
            <input
                id="firstAdj"
                name="adj"
                value={ formData.adj }
                onChange={ handleChange }
            /><label htmlFor="Word">First Adjective:</label>
            <input
                id="secondAdj"
                name="adj2"
                value={ formData.adj2 }
                onChange={ handleChange }
            />


            <button>Add a new person!</button>
            {/* <p> There was a { formData.adj2 } { formData.noun2 } who loved a { formData.adj } { formData.noun } </p> */ }
        </form>
        { handleSubmit }
    </>
    );
};

export default Form;
